import React from "react";

export default class Search extends React.Component {
    state = {
        search: '',
    }
    handValue = (e) => {
        this.setState({ search: e.target.value })
    }
    handclick = (e) => {
        if (e.key === 'Enter') {
            this.props.searchMovies(this.state.search)
        }
    }
btnclick= () =>{
    if (this.handValue){
        this.props.searchMovies(this.state.search)
    }
    else{
        this.props.searchMovies(this.state.search)

        alert('xato')

    }
}
    render() {
        const { search } = this.state
        return (
            <div className="row">
                <div className="col s12">
                    <div className="input-field">
                        <input
                            value={search}
                            placeholder='search'
                            type="search"
                            className="search"
                            onChange={this.handValue}
                            onKeyDown={this.handclick}
                        />
                        {/* <button className='btn search-btn' onClick={() => this.props.searchMovies(this.state.search)}>Search movies</button> */}
                        <button className='btn search-btn' onClick={this.btnclick}>Search movies</button>
                
                    </div>
                </div>
            </div>
        )
    }
}
